export function example(json){return json};export function listing_onchange_magic_cards(event){if(event.data.back.title)if(event.elem.querySelector(".flip").style.transform==="rotateY(180deg)")event.elem.querySelector(".flip").style.transform="rotateY(0deg)";else event.elem.querySelector(".flip").style.transform="rotateY(180deg)"};export function listing_onrender_magic_cards(event){if(!event.data.back.title)event.elem.querySelector(".back").parentElement.removeChild(event.elem.querySelector(".back"));else event.elem.style.cursor="pointer"};export function poll_to_plotly(poll){const data={};Object.values(poll).forEach(value=>{value=String.fromCharCode(65+value-1);if(!data[value])data[value]=0;data[value]++});return{data:[{x:Object.keys(data),y:Object.values(data),type:"bar"}]}};export const question2highchart=json=>{return{chart:{type:"bar",width:"300",height:"200"},title:{text:json.title},subtitle:{text:json.subtitle},xAxis:{tickLength:0,lineWidth:0,categories:json.categories,title:{text:null}},yAxis:{gridLineWidth:0,title:{text:null},labels:{enabled:false}},tooltip:{enabled:false},plotOptions:{bar:{dataLabels:{enabled:true}}},legend:{enabled:false},credits:{enabled:false},series:[{data:json.data}]}};export function upload2data(json){return json.slides?json.slides[0].data:undefined};export function userWithEmail(user){return`<span title="${user.mail}">${user.name}</span>`};