export function example(json){return json}export function listing_onchange_magic_cards(event){if(event.data.back.title)if(event.elem.querySelector(".flip").style.transform==="rotateY(0deg)")event.elem.querySelector(".flip").style.transform="rotateY(180deg)";else event.elem.querySelector(".flip").style.transform="rotateY(0deg)"}export function listing_onrender_magic_cards(event){if(!event.data.back.title)event.elem.querySelector(".back").parentElement.removeChild(event.elem.querySelector(".back"));else event.elem.style.cursor="pointer"}export function poll_to_plotly(poll){const data={};Object.values(poll).forEach(value=>{value=String.fromCharCode(65+value-1);if(!data[value])data[value]=0;data[value]++});return{data:[{x:Object.keys(data),y:Object.values(data),type:"bar"}]}}export function poll_to_highchart(poll,answers){const data=Array(answers.length).fill(0);Object.values(poll).forEach(value=>data[value-1]++);const categories=[];for(let i=0;i<answers.length;i++)categories[i]=String.fromCharCode(65+i);return{settings:{chart:{type:"column"},title:{text:""},xAxis:{categories:categories,crosshair:true},yAxis:{min:0,title:{text:"Votes"},tickInterval:1},plotOptions:{column:{pointPadding:.2,borderWidth:0}},legend:false,tooltip:false,series:[{data:data}]}}}export function upload2data(json){return json.slides?json.slides[0].data:undefined}export function userWithEmail(user){return`<span title="${user.mail}">${user.name}</span>`}